class Api::V1::ContactsController < Api::V1::ApplicationController
  respond_to :json

  def index
    # TODO Add this to the respond_with arg in place of fixture contacts
    # @contacts = Venmo.new(current_user.venmo_token, current_user.venmo_id).get_contacts.to_json

    respond_with fixture_contacts
  end

  # GET /contactss/1
  def show
    @contacts = JSON.parse(fixture_contacts).detect {|c| c["id"] == params[:id] }
    respond_with @contacts
  end

